import pandas as pd                 # used for data manipulation and analysisimport matplotlib.pyplot as plt     # matplotlib is a plotting libraryimport matplotlib.ticker as ticker  # used to format the ticks of the plotimport seaborn as sns               # seaborn is a Python data visualization library based on matplotlib# Read data from Excel filedf = pd.read_excel('data/dataset.xlsx')# Fill NaN values in the 'Country' column with a placeholder (NaN)df['Country'].fillna('NaN', inplace=True)# Group by country and sum uploadsgrouped_df = df.groupby('Country')['uploads'].sum().reset_index()# Plot using Seabornsns.barplot(x='Country', y='uploads', hue='Country', data=grouped_df, palette='viridis', legend=False)plt.xlabel('By Kubilay Dogan & Zekeriya Otumlu')plt.ylabel('')plt.title('Total')# Set the x-axis labelplt.xticks(rotation=90)# Set the y-axis ticks with a custom formatterplt.gca().yaxis.set_major_formatter(ticker.FuncFormatter(lambda x, pos: '{:,.0f}'.format(x)))plt.yticks(range(0, 4000000 + 1, 500000))# Show the Seaborn plotplt.show()